      updatedAt: now,
    };

    this.appConfigs.set(userId, appConfig);
    console.log("âœ… Config saved with keys:", Object.keys(appConfig));
    console.log("ðŸ”‘ Has access token:", !!appConfig.whatsappAccessToken);
