    console.log("ðŸ” DatabaseStorage: Looking for user:", username);
    const [user] = await db.select().from(users).where(eq(users.username, username));
    console.log("ðŸ‘¤ DatabaseStorage: User found:", user ? "yes" : "no");
    if (user) {
      console.log("ðŸ”‘ DatabaseStorage: Password hash length:", user.password.length);
    }
    return user || undefined;
  }

  async createUser(insertUser: InsertUser): Promise<User> {
    const [user] = await db
      .insert(users)
      .values({ ...insertUser, id: randomUUID(), createdAt: new Date() })
      .returning();
    return user;
  }

  // Contact operations
  async getContacts(): Promise<Contact[]> {
    return await db.select().from(contacts);
  }

  async getContact(id: string): Promise<Contact | undefined> {
    const [contact] = await db.select().from(contacts).where(eq(contacts.id, id));
    return contact || undefined;
  }

  async createContact(insertContact: InsertContact): Promise<Contact> {
    const [contact] = await db
      .insert(contacts)
      .values({ 
        ...insertContact, 
        id: randomUUID(), 
        createdAt: new Date(),
        group: insertContact.group || "customer",
        email: insertContact.email || null,
        profileImageUrl: insertContact.profileImageUrl || null,
        lastContact: insertContact.lastContact || null
      })
      .returning();
    return contact;
  }

  async updateContact(id: string, updates: Partial<InsertContact>): Promise<Contact> {
    const [contact] = await db
      .update(contacts)
      .set(updates)
      .where(eq(contacts.id, id))
      .returning();
    return contact;
  }

  async deleteContact(id: string): Promise<void> {
    await db.delete(contacts).where(eq(contacts.id, id));
  }

  // Conversation operations
  async getConversations(): Promise<ConversationWithContact[]> {
    const result = await db
      .select({
        id: conversations.id,
        contactId: conversations.contactId,
        lastMessageId: conversations.lastMessageId,
        unreadCount: conversations.unreadCount,
        updatedAt: conversations.updatedAt,
        contact: contacts
