      .update(messages)
      .set({ status })
      .where(eq(messages.id, id));
  }

  async updateMessageReadStatus(id: string, isRead: boolean, readAt: Date): Promise<void> {
    await db
      .update(messages)
      .set({ 
        isRead,
        readAt 
      })
      .where(eq(messages.id, id));
  }

  // Template operations
  async getTemplates(): Promise<Template[]> {
