import { useState, useEffect } from "react";
import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { Tabs, TabsContent, TabsList, TabsTrigger } from "@/components/ui/tabs";
import { Switch } from "@/components/ui/switch";
import { Badge } from "@/components/ui/badge";
import { Alert, AlertDescription } from "@/components/ui/alert";
import { useToast } from "@/hooks/use-toast";
import { apiRequest } from "@/lib/queryClient";

interface ConfigData {
  whatsappAccessToken: string;
  whatsappPhoneNumberId: string;
  whatsappBusinessAccountId: string;
  whatsappWebhookVerifyToken: string;
  n8nWebhookUrl: string;
  n8nApiKey: string;
  n8nEnabled: boolean;
  usePersistentDb: boolean;
  dbHost: string;
  dbPort: string;
  dbName: string;
  dbUsername: string;
  dbPassword: string;
  cdnType: string;
  bunnyApiKey: string;
  bunnyStorageZone: string;
  bunnyPullZone: string;
  bunnyRegion: string;
  cdnBaseUrl: string;
  enableLogging: boolean;
  webhookSecret: string;
  isConfigured: boolean;
}

export default function Settings() {
  const [configData, setConfigData] = useState<ConfigData>({
    whatsappAccessToken: "",
    whatsappPhoneNumberId: "",
    whatsappBusinessAccountId: "",
    whatsappWebhookVerifyToken: "",
    n8nWebhookUrl: "",
    n8nApiKey: "",
    n8nEnabled: false,
    usePersistentDb: false,
    dbHost: "",
    dbPort: "",
    dbName: "",
    dbUsername: "",
    dbPassword: "",
    cdnType: "none",
    bunnyApiKey: "",
    bunnyStorageZone: "",
    bunnyPullZone: "",
    bunnyRegion: "ny",
    cdnBaseUrl: "",
    enableLogging: true,
    webhookSecret: "",
    isConfigured: false,
  });

  const [connectionStatus, setConnectionStatus] = useState<{
    whatsapp: "unknown" | "testing" | "success" | "failed";
    n8n: "unknown" | "testing" | "success" | "failed";
  }>({ whatsapp: "unknown", n8n: "unknown" });

  const [showTokens, setShowTokens] = useState(false);

  const { toast } = useToast();
  const queryClient = useQueryClient();

  const { data: currentConfig, isLoading } = useQuery<ConfigData>({
    queryKey: ["/api/settings/config"],
  });

  // Update form data when config loads
  useEffect(() => {
    if (currentConfig) {
      setConfigData(currentConfig);
    }
  }, [currentConfig]);

  const configMutation = useMutation({
    mutationFn: async (data: ConfigData) => {
      const response = await apiRequest("POST", "/api/settings/config", data);
      return response.json();
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/settings/config"] });
      toast({
        title: "Configuration saved",
        description: "Your settings have been saved successfully.",
      });
    },
    onError: (error: Error) => {
      toast({
        title: "Failed to save configuration",
        description: error.message || "There was an error saving your settings.",
        variant: "destructive",
      });
    },
  });

  const testConnectionMutation = useMutation({
    mutationFn: async (type: "n8n" | "whatsapp") => {
      setConnectionStatus(prev => ({ ...prev, [type]: "testing" }));
      const response = await apiRequest("POST", `/api/settings/test-connection`, { type });
      return { type, result: await response.json() };
    },
    onSuccess: ({ type, result }) => {
      setConnectionStatus(prev => ({ ...prev, [type]: "success" }));
      toast({
        title: "Connection successful",
        description: result.message || "Connection test passed.",
      });
    },
    onError: (error: Error, variables) => {
      const type = (variables as any)?.type || "unknown";
      setConnectionStatus(prev => ({ ...prev, [type]: "failed" }));
      toast({
        title: "Connection failed",
        description: error.message || "Connection test failed.",
        variant: "destructive",
      });
    },
  });

  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();
    configMutation.mutate(configData);
  };

  const handleInputChange = (field: keyof ConfigData, value: string | boolean) => {
    setConfigData(prev => ({ ...prev, [field]: value }));
  };

  const getStatusBadge = (status: string) => {
    switch (status) {
      case "success":
        return <Badge variant="default" className="bg-green-500">Connected</Badge>;
      case "failed":
        return <Badge variant="destructive">Failed</Badge>;
      case "testing":
        return <Badge variant="secondary">Testing...</Badge>;
      default:
        return <Badge variant="outline">Unknown</Badge>;
    }
  };

  if (isLoading) {
    return (
      <div className="flex items-center justify-center h-full">
        <div className="text-center">
          <div className="w-8 h-8 border-2 border-primary border-t-transparent rounded-full animate-spin mx-auto mb-2"></div>
          <p className="text-muted-foreground">Loading settings...</p>
        </div>
      </div>
    );
  }

  return (
    <div className="p-6 max-w-6xl mx-auto">
      <div className="mb-6">
        <h1 className="text-3xl font-bold text-gray-900">Settings & Configuration</h1>
        <p className="text-gray-600 mt-2">Configure your WhatsApp Business integration for production use</p>

        {configData.isConfigured && (
          <Alert className="mt-4 border-green-200 bg-green-50">
            <AlertDescription className="text-green-800">
              âœ“ Your WhatsApp Business API is configured and ready to use. Messages can be sent directly through the application.
            </AlertDescription>
          </Alert>
        )}

        {configData.whatsappPhoneNumberId && !configData.whatsappAccessToken && (
          <Alert className="mt-4 border-red-200 bg-red-50">
            <AlertDescription className="text-red-800">
              âš ï¸ Missing WhatsApp Access Token! You have Phone Number ID configured but messages cannot be sent without the Access Token. Please add it in the WhatsApp Business tab.
            </AlertDescription>
          </Alert>
        )}
      </div>

      <Tabs defaultValue="whatsapp" className="space-y-6">
        <TabsList className="grid w-full grid-cols-5">
          <TabsTrigger value="whatsapp">WhatsApp Business</TabsTrigger>
          <TabsTrigger value="media">Media Server</TabsTrigger>
          <TabsTrigger value="n8n">n8n Integration</TabsTrigger>
          <TabsTrigger value="webhooks">Webhooks</TabsTrigger>
          <TabsTrigger value="system">System</TabsTrigger>
        </TabsList>

        <form onSubmit={handleSubmit}>
          <TabsContent value="whatsapp" className="space-y-6">
            <Card>
              <CardHeader className="pb-4">
                <div className="flex justify-between items-center">
                  <div>
                    <CardTitle>WhatsApp Business Cloud API</CardTitle>
                    <CardDescription>
                      Configure your WhatsApp Business Account to send messages directly
                    </CardDescription>
                  </div>
                  {getStatusBadge(connectionStatus.whatsapp)}
                </div>
              </CardHeader>
              <CardContent className="space-y-6">
                <div className="bg-blue-50 p-4 rounded-lg">
                  <h4 className="font-medium text-blue-900 mb-2">Quick Setup Guide:</h4>
                  <ol className="text-sm text-blue-800 space-y-1 list-decimal list-inside">
                    <li>Create a Facebook App with WhatsApp Business product</li>
                    <li>Get your Access Token from the Facebook App dashboard</li>
                    <li>Find your Phone Number ID in WhatsApp Business Account</li>
                    <li>Configure webhook verification token</li>
                    <li>Test the connection below</li>
                  </ol>
                </div>

                <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
                  <div>
                    <Label htmlFor="whatsappAccessToken">Access Token *</Label>
                    <div className="flex space-x-2">
                      <Input
                        id="whatsappAccessToken"
                        type={showTokens ? "text" : "password"}
                        value={configData.whatsappAccessToken || ""}
                        onChange={(e) => handleInputChange("whatsappAccessToken", e.target.value)}
                        placeholder="EAAxxxxxxxx..."
                        data-testid="input-access-token"
                        className="flex-1"
                      />
                      <Button
                        type="button"
                        variant="outline"
                        size="icon"
                        onClick={() => setShowTokens(!showTokens)}
                        data-testid="button-toggle-tokens"
                      >
                        <i className={`fas ${showTokens ? "fa-eye-slash" : "fa-eye"}`} />
                      </Button>
                    </div>
                    <p className="text-xs text-gray-500 mt-1">From your Facebook App dashboard</p>
                  </div>

                  <div>
                    <Label htmlFor="whatsappPhoneNumberId">Phone Number ID *</Label>
                    <Input
                      id="whatsappPhoneNumberId"
                      value={configData.whatsappPhoneNumberId || ""}
                      onChange={(e) => handleInputChange("whatsappPhoneNumberId", e.target.value)}
                      placeholder="123456789012345"
                      data-testid="input-phone-number-id"
                    />
                    <p className="text-xs text-blue-600 mt-1">
                      ðŸ“‹ From webhook logs, your correct Phone Number ID should be: <strong>776001938919357</strong>
                    </p>
                    <p className="text-xs text-gray-500 mt-1">From WhatsApp Business Account</p>
                  </div>

                  <div>
                    <Label htmlFor="whatsappBusinessAccountId">Business Account ID</Label>
                    <Input
                      id="whatsappBusinessAccountId"
                      value={configData.whatsappBusinessAccountId || ""}
                      onChange={(e) => handleInputChange("whatsappBusinessAccountId", e.target.value)}
                      placeholder="732324892861637"
                      data-testid="input-business-account-id"
                    />
                    <p className="text-xs text-blue-600 mt-1">
                      ðŸ“‹ From webhook logs, your Business Account ID should be: <strong>732324892861637</strong>
                    </p>
                    <p className="text-xs text-gray-500 mt-1">Required for template sync from Facebook Business Manager</p>
                  </div>

                  <div>
                    <Label htmlFor="whatsappWebhookVerifyToken">Webhook Verify Token</Label>
                    <Input
                      id="whatsappWebhookVerifyToken"
                      value={configData.whatsappWebhookVerifyToken || ""}
                      onChange={(e) => handleInputChange("whatsappWebhookVerifyToken", e.target.value)}
                      placeholder="my_secure_token_123"
                      data-testid="input-webhook-verify-token"
                    />
                    <p className="text-xs text-gray-500 mt-1">Custom token for webhook security</p>
                  </div>
                </div>

                <div className="flex justify-between items-center pt-4 border-t">
                  <div className="text-sm text-gray-600">
                    Status: {connectionStatus.whatsapp === "success" ? "âœ“ Connected" : 
                            connectionStatus.whatsapp === "failed" ? "âœ— Failed" : "Not tested"}
                  </div>
                  <Button
                    type="button"
                    variant="outline"
                    onClick={() => testConnectionMutation.mutate("whatsapp")}
                    disabled={testConnectionMutation.isPending || !configData.whatsappAccessToken || !configData.whatsappPhoneNumberId}
                    data-testid="button-test-whatsapp"
                  >
                    {testConnectionMutation.isPending ? "Testing..." : "Test Connection"}
                  </Button>
                </div>
              </CardContent>
            </Card>
          </TabsContent>

          <TabsContent value="media" className="space-y-6">
            <Card>
              <CardHeader>
                <CardTitle>Media Server & CDN Configuration</CardTitle>
                <CardDescription>
                  Configure your CDN or media server for WhatsApp media files (images, videos, documents)
                </CardDescription>
              </CardHeader>
              <CardContent className="space-y-6">
                <div className="bg-orange-50 p-4 rounded-lg">
                  <h4 className="font-medium text-orange-900 mb-2">Supported CDN Providers:</h4>
                  <ul className="text-sm text-orange-800 space-y-1 list-disc list-inside">
                    <li>Bunny CDN - High performance global CDN</li>
                    <li>AWS S3 + CloudFront - Enterprise solution</li>
                    <li>Cloudinary - Image and video optimization</li>
                    <li>Custom CDN - Your own media server</li>
                  </ul>
                </div>

                <div className="space-y-4">
                  <div>
                    <Label htmlFor="cdnType">CDN Provider</Label>
                    <select
                      id="cdnType"
                      value={configData.cdnType || "none"}
                      onChange={(e) => handleInputChange("cdnType", e.target.value)}
                      className="w-full p-2 border border-gray-300 rounded-md"
                      data-testid="select-cdn-type"
                    >
                      <option value="none">No CDN (Basic media handling)</option>
                      <option value="bunny">Bunny CDN</option>
                      <option value="aws">AWS S3 + CloudFront</option>
                      <option value="cloudinary">Cloudinary</option>
                      <option value="custom">Custom CDN</option>
                    </select>
                    <p className="text-xs text-gray-500 mt-1">Choose your preferred media hosting provider</p>
                  </div>

                  {configData.cdnType === "bunny" && (
                    <div className="space-y-4 border-l-4 border-orange-200 pl-4 bg-orange-50 p-4 rounded">
                      <h4 className="font-medium text-orange-900">Bunny CDN Configuration</h4>
                      <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                        <div>
                          <Label htmlFor="bunnyApiKey">API Key</Label>
                          <Input
                            id="bunnyApiKey"
                            type={showTokens ? "text" : "password"}
                            value={configData.bunnyApiKey || ""}
                            onChange={(e) => handleInputChange("bunnyApiKey", e.target.value)}
                            placeholder="Your Bunny CDN API key"
                            data-testid="input-bunny-api-key"
                          />
                        </div>
                        <div>
                          <Label htmlFor="bunnyStorageZone">Storage Zone</Label>
                          <Input
                            id="bunnyStorageZone"
                            value={configData.bunnyStorageZone || ""}
                            onChange={(e) => handleInputChange("bunnyStorageZone", e.target.value)}
                            placeholder="your-storage-zone"
                            data-testid="input-bunny-storage-zone"
                          />
                        </div>
                        <div>
                          <Label htmlFor="bunnyPullZone">Pull Zone</Label>
                          <Input
                            id="bunnyPullZone"
                            value={configData.bunnyPullZone || ""}
                            onChange={(e) => handleInputChange("bunnyPullZone", e.target.value)}
                            placeholder="your-pull-zone"
                            data-testid="input-bunny-pull-zone"
                          />
                        </div>
                        <div>
                          <Label htmlFor="bunnyRegion">Region</Label>
                          <select
                            id="bunnyRegion"
                            value={configData.bunnyRegion || "ny"}
                            onChange={(e) => handleInputChange("bunnyRegion", e.target.value)}
                            className="w-full p-2 border border-gray-300 rounded-md"
                            data-testid="select-bunny-region"
                          >
                            <option value="ny">New York (US East)</option>
                            <option value="la">Los Angeles (US West)</option>
                            <option value="sg">Singapore (Asia)</option>
                            <option value="sy">Sydney (Australia)</option>
                            <option value="br">SÃ£o Paulo (Brazil)</option>
                            <option value="de">Frankfurt (Europe)</option>
                          </select>
                        </div>
                      </div>
                    </div>
                  )}

                  {configData.cdnType !== "none" && configData.cdnType !== "bunny" && (
                    <div className="space-y-4 border-l-4 border-blue-200 pl-4 bg-blue-50 p-4 rounded">
                      <h4 className="font-medium text-blue-900">CDN Base URL</h4>
                      <div>
                        <Label htmlFor="cdnBaseUrl">Base URL</Label>
                        <Input
                          id="cdnBaseUrl"
                          value={configData.cdnBaseUrl || ""}
                          onChange={(e) => handleInputChange("cdnBaseUrl", e.target.value)}
                          placeholder="https://your-cdn-domain.com"
                          data-testid="input-cdn-base-url"
                        />
                        <p className="text-xs text-gray-500 mt-1">The base URL for your CDN or media server</p>
                      </div>
                    </div>
                  )}
                </div>

                <div className="bg-green-50 p-4 rounded-lg">
                  <h4 className="font-medium text-green-900 mb-2">Media Features:</h4>
                  <ul className="text-sm text-green-800 space-y-1 list-disc list-inside">
                    <li>Automatic media upload from WhatsApp webhooks</li>
                    <li>Image and video preview in chat interface</li>
                    <li>Optimized media delivery and caching</li>
                    <li>Thumbnail generation for videos</li>
                    <li>Download links for documents</li>
                  </ul>
                </div>

                <div className="flex justify-between items-center pt-4 border-t">
                  <div className="text-sm text-gray-600">
                    CDN Status: {connectionStatus.cdn === "success" ? "âœ“ Connected" : 
                               connectionStatus.cdn === "failed" ? "âœ— Failed" : "Not tested"}
                  </div>
                  <Button
                    type="button"
                    variant="outline"
                    onClick={() => testConnectionMutation.mutate("cdn")}
                    disabled={testConnectionMutation.isPending || configData.cdnType === "none"}
                    data-testid="button-test-cdn"
                  >
                    {testConnectionMutation.isPending ? "Testing..." : "Test CDN Connection"}
                  </Button>
                </div>
              </CardContent>
            </Card>
          </TabsContent>

          <TabsContent value="n8n" className="space-y-6">
            <Card>
              <CardHeader className="pb-4">
                <div className="flex justify-between items-center">
                  <div>
                    <CardTitle>n8n Integration (Optional)</CardTitle>
                    <CardDescription>
                      Connect with n8n for advanced automation workflows
                    </CardDescription>
                  </div>
                  <div className="flex items-center space-x-2">
                    <Switch
                      checked={configData.n8nEnabled}
                      onCheckedChange={(checked) => handleInputChange("n8nEnabled", checked)}
                      data-testid="switch-n8n-enabled"
                    />
                    {getStatusBadge(connectionStatus.n8n)}
                  </div>
                </div>
              </CardHeader>
              <CardContent className="space-y-6">
                <div className="bg-purple-50 p-4 rounded-lg">
                  <h4 className="font-medium text-purple-900 mb-2">n8n Features:</h4>
                  <ul className="text-sm text-purple-800 space-y-1 list-disc list-inside">
                    <li>Advanced workflow automation</li>
                    <li>Multi-step marketing campaigns</li>
                    <li>External system integrations</li>
                    <li>Conditional message routing</li>
                  </ul>
                </div>

                <div className="space-y-4">
                  <div>
                    <Label htmlFor="n8nWebhookUrl">n8n Webhook URL</Label>
                    <Input
                      id="n8nWebhookUrl"
                      value={configData.n8nWebhookUrl || ""}
                      onChange={(e) => handleInputChange("n8nWebhookUrl", e.target.value)}
                      placeholder="https://your-n8n-instance.com/webhook/whatsapp"
                      data-testid="input-n8n-webhook-url"
                      disabled={!configData.n8nEnabled}
                    />
                    <p className="text-xs text-gray-500 mt-1">Your n8n webhook endpoint URL</p>
                  </div>

                  <div>
                    <Label htmlFor="n8nApiKey">n8n API Key (Optional)</Label>
                    <Input
                      id="n8nApiKey"
                      type={showTokens ? "text" : "password"}
                      value={configData.n8nApiKey || ""}
                      onChange={(e) => handleInputChange("n8nApiKey", e.target.value)}
                      placeholder="n8n_api_key_xxx"
                      data-testid="input-n8n-api-key"
                      disabled={!configData.n8nEnabled}
                    />
                    <p className="text-xs text-gray-500 mt-1">For advanced n8n API operations</p>
                  </div>
                </div>

                <div className="flex justify-between items-center pt-4 border-t">
                  <div className="text-sm text-gray-600">
                    Status: {connectionStatus.n8n === "success" ? "âœ“ Connected" : 
                            connectionStatus.n8n === "failed" ? "âœ— Failed" : "Not tested"}
                  </div>
                  <Button
                    type="button"
                    variant="outline"
                    onClick={() => testConnectionMutation.mutate("n8n")}
                    disabled={testConnectionMutation.isPending || !configData.n8nEnabled || !configData.n8nWebhookUrl}
                    data-testid="button-test-n8n"
                  >
                    {testConnectionMutation.isPending ? "Testing..." : "Test Connection"}
                  </Button>
                </div>
              </CardContent>
            </Card>
          </TabsContent>

          <TabsContent value="webhooks" className="space-y-6">
            <Card>
              <CardHeader>
                <CardTitle>Webhook Configuration</CardTitle>
                <CardDescription>
                  Webhook endpoints for receiving WhatsApp messages and status updates
                </CardDescription>
              </CardHeader>
              <CardContent className="space-y-6">
                <div className="bg-blue-50 p-4 rounded-lg">
                  <h4 className="font-medium text-blue-900 mb-2">Setup Instructions:</h4>
                  <ol className="text-sm text-blue-800 space-y-1 list-decimal list-inside">
                    <li>Go to Facebook Developer Console â†’ Your App â†’ WhatsApp â†’ Configuration</li>
                    <li>Add the webhook URL below to "Callback URL"</li>
                    <li>Use the webhook secret as "Verify Token"</li>
                    <li>Subscribe to "messages" webhook field</li>
                    <li>Click "Verify and Save" to complete setup</li>
                  </ol>
                </div>

                <div className="space-y-4">
                  <div>
                    <Label>Webhook URL (Copy this to Facebook Developer Console)</Label>
                    <div className="flex space-x-2">
                      <Input
                        value={typeof window !== 'undefined' ? `${window.location.origin}/api/webhooks/whatsapp` : ''}
                        readOnly
                        className="bg-gray-50 font-mono text-sm"
                      />
                      <Button
                        type="button"
                        variant="outline"
                        size="icon"
                        onClick={() => {
                          const url = `${window.location.origin}/api/webhooks/whatsapp`;
                          navigator.clipboard.writeText(url);
                        }}
                        data-testid="button-copy-webhook-url"
                      >
                        ðŸ“‹
                      </Button>
                    </div>
                  </div>

                  <div>
                    <Label>Webhook Secret (Use as Verify Token)</Label>
                    <div className="flex space-x-2">
                      <Input
                        value={configData.webhookSecret || "webhook_verify_token_123"}
                        readOnly
                        className="bg-gray-50 font-mono text-sm"
                      />
                      <Button
                        type="button"
                        variant="outline"
                        size="icon"
                        onClick={() => {
                          navigator.clipboard.writeText(configData.webhookSecret || "webhook_verify_token_123");
                        }}
                        data-testid="button-copy-webhook-secret"
                      >
                        ðŸ“‹
                      </Button>
                    </div>
                    <p className="text-xs text-gray-500 mt-1">Copy this exact value to the "Verify Token" field in Facebook Developer Console</p>
                  </div>
                </div>

                <div className="bg-amber-50 p-4 rounded-lg">
                  <h4 className="font-medium text-amber-900 mb-2">Important Notes:</h4>
                  <ul className="text-sm text-amber-800 space-y-1 list-disc list-inside">
                    <li>Your webhook URL must be publicly accessible (not localhost)</li>
                    <li>Use HTTPS in production (required by WhatsApp)</li>
                    <li>Webhook secret must match exactly between here and Facebook console</li>
                    <li>After setup, send a test message to your WhatsApp Business number</li>
                  </ul>
                </div>
              </CardContent>
            </Card>
          </TabsContent>

          <TabsContent value="system" className="space-y-6">
            <Card>
              <CardHeader>
                <CardTitle>Database Configuration</CardTitle>
                <CardDescription>
                  Configure database storage for persistent data
                </CardDescription>
              </CardHeader>
              <CardContent className="space-y-6">
                <div className="flex items-center justify-between">
                  <div>
                    <Label htmlFor="usePersistentDb">Use PostgreSQL Database</Label>
                    <p className="text-sm text-gray-500">Enable persistent storage instead of in-memory storage</p>
                  </div>
                  <Switch
                    id="usePersistentDb"
                    checked={configData.usePersistentDb || false}
                    onCheckedChange={(checked) => handleInputChange("usePersistentDb", checked)}
                    data-testid="switch-persistent-db"
                  />
                </div>

                {configData.usePersistentDb && (
                  <div className="space-y-4 border-l-4 border-blue-200 pl-4 bg-blue-50 p-4 rounded">
                    <h4 className="font-medium text-blue-900">Database Connection Details</h4>
                    <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                      <div>
                        <Label htmlFor="dbHost">Host</Label>
                        <Input
                          id="dbHost"
                          value={configData.dbHost || ""}
                          onChange={(e) => handleInputChange("dbHost", e.target.value)}
                          placeholder="localhost or IP address"
                          data-testid="input-db-host"
                        />
                      </div>
                      <div>
                        <Label htmlFor="dbPort">Port</Label>
                        <Input
                          id="dbPort"
                          type="number"
                          value={configData.dbPort || ""}
                          onChange={(e) => handleInputChange("dbPort", e.target.value)}
                          placeholder="5432"
                          data-testid="input-db-port"
                        />
                      </div>
                      <div>
                        <Label htmlFor="dbName">Database Name</Label>
                        <Input
                          id="dbName"
                          value={configData.dbName || ""}
                          onChange={(e) => handleInputChange("dbName", e.target.value)}
                          placeholder="whatsapp_business"
                          data-testid="input-db-name"
                        />
                      </div>
                      <div>
                        <Label htmlFor="dbUsername">Username</Label>
                        <Input
                          id="dbUsername"
                          value={configData.dbUsername || ""}
                          onChange={(e) => handleInputChange("dbUsername", e.target.value)}
                          placeholder="postgres"
                          data-testid="input-db-username"
                        />
                      </div>
                      <div className="md:col-span-2">
                        <Label htmlFor="dbPassword">Password</Label>
                        <Input
                          id="dbPassword"
                          type="password"
                          value={configData.dbPassword || ""}
                          onChange={(e) => handleInputChange("dbPassword", e.target.value)}
                          placeholder="Enter database password"
                          data-testid="input-db-password"
                        />
                      </div>
                    </div>
                    <div className="bg-amber-50 p-3 rounded border border-amber-200">
                      <p className="text-xs text-amber-800">
                        <strong>Note:</strong> Enabling PostgreSQL will persist all chat conversations, contacts, and settings. 
                        Data will be preserved between sessions and server restarts.
                      </p>
                    </div>
                  </div>
                )}
              </CardContent>
            </Card>

            <Card>
              <CardHeader>
                <CardTitle>System Configuration</CardTitle>
                <CardDescription>
                  General system settings and preferences
                </CardDescription>
              </CardHeader>
              <CardContent className="space-y-6">
                <div className="flex items-center justify-between">
                  <div>
                    <Label htmlFor="enableLogging">Enable Logging</Label>
                    <p className="text-sm text-gray-500">Log webhook events and API calls for debugging</p>
                  </div>
                  <Switch
                    id="enableLogging"
                    checked={configData.enableLogging}
                    onCheckedChange={(checked) => handleInputChange("enableLogging", checked)}
                    data-testid="switch-enable-logging"
                  />
                </div>

                <div className="border-t pt-4">
                  <h4 className="font-medium mb-2">System Status</h4>
                  <div className="grid grid-cols-2 gap-4 text-sm">
                    <div className="flex justify-between">
                      <span>WhatsApp API:</span>
                      <span className={configData.isConfigured ? "text-green-600" : "text-red-600"}>
                        {configData.isConfigured ? "Configured" : "Not Configured"}
                      </span>
                    </div>
                    <div className="flex justify-between">
                      <span>n8n Integration:</span>
                      <span className={configData.n8nEnabled ? "text-blue-600" : "text-gray-500"}>
                        {configData.n8nEnabled ? "Enabled" : "Disabled"}
                      </span>
                    </div>
                  </div>
                </div>
              </CardContent>
            </Card>
          </TabsContent>

          <div className="flex justify-end space-x-4 pt-6">
            <Button
              type="submit"
              disabled={configMutation.isPending}
              data-testid="button-save-config"
              className="px-8"
            >
              {configMutation.isPending ? "Saving..." : "Save Configuration"}
            </Button>
          </div>
        </form>
      </Tabs>
    </div>
  );
}

client/src/components/broadcast/broadcas.content:
import { useState, useRef } from "react";
import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { Checkbox } from "@/components/ui/checkbox";
import { Badge } from "@/components/ui/badge";
import { Textarea } from "@/components/ui/textarea";
import { Dialog, DialogContent, DialogDescription, DialogHeader, DialogTitle, DialogTrigger } from "@/components/ui/dialog";
import { useToast } from "@/hooks/use-toast";
import { apiRequest } from "@/lib/queryClient";
import { Template, Contact, Broadcast } from "@shared/schema";

export default function BroadcastComposer() {
  const [selectedTemplateId, setSelectedTemplateId] = useState<string>("");
  const [selectedContacts, setSelectedContacts] = useState<Set<string>>(new Set());
  const [variables, setVariables] = useState<Record<string, string>>({});
  const [scheduledFor, setScheduledFor] = useState<string>("");
  const [isScheduled, setIsScheduled] = useState(false);
  const [csvData, setCsvData] = useState<any[]>([]);
  const [isCSVDialogOpen, setIsCSVDialogOpen] = useState(false);
  const [csvUploadMode, setCSVUploadMode] = useState(false);
  const fileInputRef = useRef<HTMLInputElement>(null);

  const { toast } = useToast();
  const queryClient = useQueryClient();

  const { data: templates = [] } = useQuery<Template[]>({
    queryKey: ["/api/templates"],
  });

  const { data: contacts = [] } = useQuery<Contact[]>({
    queryKey: ["/api/contacts"],
  });

  const { data: broadcasts = [] } = useQuery<Broadcast[]>({
    queryKey: ["/api/broadcasts"],
  });

  const broadcastMutation = useMutation({
    mutationFn: async (data: any) => {
      const response = await apiRequest("POST", "/api/broadcasts", data);
      return response.json();
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/broadcasts"] });
      resetForm();
      toast({
        title: "Broadcast sent",
        description: "Your broadcast has been queued for delivery.",
      });
    },
    onError: (error: Error) => {
      toast({
        title: "Failed to send broadcast",
        description: error.message || "There was an error sending your broadcast.",
        variant: "destructive",
      });
    },
  });

  const selectedTemplate = templates.find(t => t.id === selectedTemplateId);
  const templateVariables = (selectedTemplate?.variables && Array.isArray(selectedTemplate.variables)) ? selectedTemplate.variables : [];

  const resetForm = () => {
    setSelectedTemplateId("");
    setSelectedContacts(new Set());
    setVariables({});
    setScheduledFor("");
    setIsScheduled(false);
    setCsvData([]);
    setCSVUploadMode(false);
  };

  const handleCSVUpload = (event: React.ChangeEvent<HTMLInputElement>) => {
    const file = event.target.files?.[0];
    if (file && file.type === "text/csv") {
      const reader = new FileReader();
      reader.onload = (e) => {
        const csv = e.target?.result as string;
        const lines = csv.split('\n').filter(line => line.trim());
        const headers = lines[0].split(',').map(h => h.trim());

        const data = lines.slice(1).map(line => {
          const values = line.split(',').map(v => v.trim());
          const row: any = {};
          headers.forEach((header, index) => {
            row[header] = values[index] || "";
          });
          return row;
        });

        setCsvData(data);
        setCSVUploadMode(true);
        setIsCSVDialogOpen(false);
        toast({
          title: "CSV uploaded",
          description: `Loaded ${data.length} contacts from CSV file.`,
        });
      };
      reader.readAsText(file);
    } else {
      toast({
        title: "Invalid file",
        description: "Please upload a valid CSV file.",
        variant: "destructive",
      });
    }
  };

  const handleContactToggle = (contactId: string) => {
    const newSelected = new Set(selectedContacts);
    if (newSelected.has(contactId)) {
      newSelected.delete(contactId);
    } else {
      newSelected.add(contactId);
    }
    setSelectedContacts(newSelected);
  };

  const handleSelectAll = () => {
    if (selectedContacts.size === contacts.length) {
      setSelectedContacts(new Set());
    } else {
      setSelectedContacts(new Set(contacts.map(c => c.id)));
    }
  };

  const handleVariableChange = (variable: string, value: string) => {
    setVariables(prev => ({ ...prev, [variable]: value }));
  };

  const renderPreview = () => {
    if (!selectedTemplate) return "";

    let preview = selectedTemplate.content;
    Object.entries(variables).forEach(([key, value]) => {
      preview = preview.replace(new RegExp(`\\{\\{${key}\\}\\}`, 'g'), value || `{{${key}}}`);
    });

    return preview;
  };

  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();

    if (!selectedTemplateId) {
      toast({
        title: "Template required",
        description: "Please select a template for your broadcast.",
        variant: "destructive",
      });
      return;
    }

    if (!csvUploadMode && selectedContacts.size === 0) {
      toast({
        title: "Recipients required",
        description: "Please select at least one recipient for your broadcast.",
        variant: "destructive",
      });
      return;
    }

    if (csvUploadMode && csvData.length === 0) {
      toast({
        title: "CSV data required",
        description: "Please upload a CSV file with recipient data.",
        variant: "destructive",
      });
      return;
    }

    // Check if all required variables are filled
    const missingVariables = templateVariables.filter(variable => !variables[variable]?.trim());
    if (missingVariables.length > 0) {
      toast({
        title: "Missing variables",
        description: `Please fill in all template variables: ${missingVariables.join(", ")}`,
        variant: "destructive",
      });
      return;
    }

    const broadcastData = {
      templateId: selectedTemplateId,
      recipients: csvUploadMode ? csvData.map((row, index) => `csv-${index}`) : Array.from(selectedContacts),
      variables: csvUploadMode ? {} : variables,
      csvData: csvUploadMode ? csvData : null,
      scheduledFor: isScheduled && scheduledFor ? new Date(scheduledFor) : null,
    };

    broadcastMutation.mutate(broadcastData);
  };

  const formatBroadcastTime = (date: Date) => {
    return new Date(date).toLocaleString("en-US", {
      month: "short",
      day: "numeric",
      hour: "numeric",
      minute: "2-digit",
      hour12: true,
    });
  };

  return (
    <div className="p-6 max-w-6xl mx-auto">
      <div className="mb-6">
        <h2 className="text-2xl font-bold text-gray-800">Bulk Broadcast</h2>
        <p className="text-gray-600 mt-1">Send template messages to multiple contacts</p>
      </div>

      <div className="grid grid-cols-1 xl:grid-cols-3 gap-6">
        {/* Broadcast Composer */}
        <div className="xl:col-span-2">
          <Card>
            <CardHeader>
              <CardTitle>Compose Broadcast</CardTitle>
              <CardDescription>Select a template, choose recipients, and send your message</CardDescription>
            </CardHeader>
            <CardContent>
              <form onSubmit={handleSubmit} className="space-y-6">
                {/* Template Selection */}
                <div>
                  <Label htmlFor="template">Select Template</Label>
                  <Select value={selectedTemplateId} onValueChange={setSelectedTemplateId}>
                    <SelectTrigger data-testid="select-broadcast-template">
                      <SelectValue placeholder="Choose a template..." />
                    </SelectTrigger>
                    <SelectContent>
                      {templates.map((template) => (
                        <SelectItem key={template.id} value={template.id}>
                          {template.name} ({template.category})
                        </SelectItem>
                      ))}
                    </SelectContent>
                  </Select>
                </div>

                {/* Template Variables */}
                {templateVariables.length > 0 && (
                  <div>
                    <Label>Template Variables</Label>
                    <div className="grid grid-cols-1 md:grid-cols-2 gap-4 mt-2">
                      {templateVariables.map((variable) => (
                        <div key={variable}>
                          <Input
                            placeholder={`{{${variable}}}`}
                            value={variables[variable] || ""}
                            onChange={(e) => handleVariableChange(variable, e.target.value)}
                            data-testid={`input-variable-${variable}`}
                          />
                        </div>
                      ))}
                    </div>
                  </div>
                )}

                {/* Recipients */}
                <div>
                  <div className="flex items-center justify-between mb-3">
                    <Label>Recipients</Label>
                    <div className="flex items-center space-x-2">
                      {!csvUploadMode && (
                        <Button
                          type="button"
                          variant="outline"
                          size="sm"
                          onClick={handleSelectAll}
                          data-testid="button-select-all-contacts"
                        >
                          {selectedContacts.size === contacts.length ? "Deselect All" : "Select All"}
                        </Button>
                      )}
                      <Dialog open={isCSVDialogOpen} onOpenChange={setIsCSVDialogOpen}>
                        <DialogTrigger asChild>
                          <Button type="button" variant="outline" size="sm" data-testid="button-upload-csv">
                            <i className="fas fa-upload mr-2" />
                            Upload CSV
                          </Button>
                        </DialogTrigger>
                        <DialogContent>
                          <DialogHeader>
                            <DialogTitle>Upload CSV File</DialogTitle>
                            <DialogDescription>
                              Upload a CSV file with contact details and template variables. 
                              Required columns: phone. Optional: name, variables (matching template variables), cta_url.
                            </DialogDescription>
                          </DialogHeader>
                          <div className="space-y-4">
                            <div>
                              <Label>CSV File</Label>
                              <Input
                                type="file"
                                accept=".csv"
                                onChange={handleCSVUpload}
                                ref={fileInputRef}
                                data-testid="input-csv-file"
                              />
                              <p className="text-sm text-gray-500 mt-1">
                                CSV format: phone,name,variable1,variable2,cta_url
                              </p>
                            </div>
                            <div className="bg-yellow-50 p-3 rounded-md">
                              <h4 className="font-medium text-yellow-800 mb-1">CSV Format Example:</h4>
                              <code className="text-sm text-yellow-700">
                                phone,name,customer_name,offer_amount,cta_url<br/>
                                +1234567890,John Doe,John,25%,https://example.com/offer1<br/>
                                +0987654321,Jane Smith,Jane,30%,https://example.com/offer2
                              </code>
                            </div>
                          </div>
                        </DialogContent>
                      </Dialog>
                      {csvUploadMode && (
                        <Button
                          type="button"
                          variant="outline"
                          size="sm"
                          onClick={() => {
                            setCsvData([]);
                            setCSVUploadMode(false);
                            if (fileInputRef.current) {
                              fileInputRef.current.value = "";
                            }
                          }}
                          data-testid="button-clear-csv"
                        >
                          Clear CSV
                        </Button>
                      )}
                      <span className="text-sm text-gray-500">
                        {csvUploadMode ? `${csvData.length} from CSV` : `${selectedContacts.size} selected`}
                      </span>
                    </div>
                  </div>

                  <div className="border border-gray-300 rounded-lg p-4 max-h-60 overflow-y-auto">
                    {csvUploadMode ? (
                      csvData.length === 0 ? (
                        <p className="text-gray-500 text-center py-4">No CSV data loaded</p>
                      ) : (
                        <div className="space-y-3">
                          {csvData.map((row, index) => (
                            <div key={index} className="flex items-center space-x-3">
                              <div className="flex-1">
                                <div className="font-medium text-gray-900">{row.name || 'N/A'}</div>
                                <div className="text-sm text-gray-500">{row.phone}</div>
                                {Object.keys(row).filter(key => !['phone', 'name'].includes(key)).map(key => (
                                  <div key={key} className="text-xs text-gray-400">
                                    {key}: {row[key]}
                                  </div>
                                ))}
                              </div>
                            </div>
                          ))}
                        </div>
                      )
                    ) : (
                      contacts.length === 0 ? (
                        <p className="text-gray-500 text-center py-4">No contacts available</p>
                      ) : (
                        <div className="space-y-3">
                          {contacts.map((contact) => (
                            <div key={contact.id} className="flex items-center space-x-3">
                              <Checkbox
                                id={`contact-${contact.id}`}
                                checked={selectedContacts.has(contact.id)}
                                onCheckedChange={() => handleContactToggle(contact.id)}
                                data-testid={`checkbox-contact-${contact.id}`}
                              />
                              <img
                                src={contact.profileImageUrl || "https://images.unsplash.com/photo-1507003211169-0a1dd7228f2d?ixlib=rb-4.0.3&w=32&h=32&fit=crop&crop=face"}
                                alt={contact.name}
                                className="w-8 h-8 rounded-full object-cover"
                              />
                              <div className="flex-1">
                                <div className="font-medium text-gray-900">{contact.name}</div>
                                <div className="text-sm text-gray-500">{contact.phone}</div>
                              </div>
                              <Badge variant="outline">{contact.group || "customer"}</Badge>
                            </div>
                          ))}
                        </div>
                      )
                    )}
                  </div>

                  <div className="mt-2 text-sm text-gray-600">
                    {selectedContacts.size} contacts selected
                  </div>
                </div>

                {/* Schedule Options */}
                <div className="space-y-3">
                  <div className="flex items-center space-x-2">
                    <Checkbox
                      id="schedule"
                      checked={isScheduled}
                      onCheckedChange={setIsScheduled}
                      data-testid="checkbox-schedule-broadcast"
                    />
                    <Label htmlFor="schedule">Schedule for later</Label>
                  </div>

                  {isScheduled && (
                    <Input
                      type="datetime-local"
                      value={scheduledFor}
                      onChange={(e) => setScheduledFor(e.target.value)}
                      min={new Date().toISOString().slice(0, 16)}
                      data-testid="input-schedule-time"
                    />
                  )}
                </div>

                {/* Submit Button */}
                <div className="flex justify-end pt-4 border-t">
                  <Button
                    type="submit"
                    className="whatsapp-green"
                    disabled={broadcastMutation.isPending}
                    data-testid="button-send-broadcast"
                  >
                    {broadcastMutation.isPending ? (
                      <>
                        <div className="w-4 h-4 border-2 border-white border-t-transparent rounded-full animate-spin mr-2" />
                        Sending...
                      </>
                    ) : (
                      <>
                        <i className="fas fa-paper-plane mr-2" />
                        {isScheduled ? "Schedule Broadcast" : "Send Broadcast"}
                      </>
                    )}
                  </Button>
                </div>
              </form>
            </CardContent>
          </Card>
        </div>

        {/* Preview and History */}
        <div className="space-y-6">
          {/* Message Preview */}
          {selectedTemplate && (
            <Card>
              <CardHeader>
                <CardTitle className="text-lg">Message Preview</CardTitle>
              </CardHeader>
              <CardContent>
                <div className="bg-gray-50 p-4 rounded-lg border">
                  <div className="message-bubble-sent rounded-lg p-3 max-w-full">
                    <p className="text-gray-800 whitespace-pre-wrap break-words">
                      {renderPreview()}
                    </p>
                    <span className="text-xs text-gray-500 mt-1 block">Preview</span>
                  </div>
                </div>
              </CardContent>
            </Card>
          )}

          {/* Broadcast History */}
          <Card>
            <CardHeader>
              <CardTitle className="text-lg">Recent Broadcasts</CardTitle>
            </CardHeader>
            <CardContent>
              {broadcasts.length === 0 ? (
                <p className="text-gray-500 text-center py-4">No broadcasts yet</p>
              ) : (
                <div className="space-y-3">
                  {broadcasts.slice(0, 5).map((broadcast) => {
                    const template = templates.find(t => t.id === broadcast.templateId);
                    return (
                      <div
                        key={broadcast.id}
                        className="p-3 border rounded-lg"
                        data-testid={`broadcast-history-${broadcast.id}`}
                      >
                        <div className="flex items-center justify-between">
                          <div>
                            <h4 className="font-medium text-gray-900">
                              {template?.name || "Unknown Template"}
                            </h4>
                            <div className="text-sm text-gray-600">
                              {Array.isArray(broadcast.recipients) ? broadcast.recipients.length : 0} recipients â€¢{" "}
                              {formatBroadcastTime(broadcast.createdAt || new Date())}
                            </div>
                          </div>
                          <Badge variant={broadcast.status === "completed" ? "default" : "secondary"}>
                            {broadcast.status}
                          </Badge>
                        </div>

                        {broadcast.status === "completed" && (
                          <div className="mt-2 text-sm text-gray-600">
                            <span className="text-green-600">
                              {broadcast.deliveredCount || 0} delivered
                            </span>
                            {(broadcast.failedCount || 0) > 0 && (
                              <span className="text-red-600 ml-2">
                                {broadcast.failedCount} failed
                              </span>
                            )}
                          </div>
                        )}
                      </div>
                    );
                  })}
                </div>
              )}
            </CardContent>
          </Card>
        </div>
      </div>
    </div>
  );
}

client/src/components/chat/chat-interfac.content:
import { useState, useRef } from "react";
import { useQuery } from "@tanstack/react-query";
import ChatList from "./chat-list";
import MessageBubble from "./message-bubble";
import TemplateVariableDialog from "@/components/chat/template-variable-dialog";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { useToast } from "@/hooks/use-toast";
import { apiRequest } from "@/lib/queryClient";
import { Message, ConversationWithContact, Template } from "@shared/schema";

interface ChatInterfaceProps {
  selectedConversationId: string | null;
  onConversationSelect: (id: string) => void;
}

export default function ChatInterface({ selectedConversationId, onConversationSelect }: ChatInterfaceProps) {
  const [messageText, setMessageText] = useState("");
  const [isSending, setIsSending] = useState(false);
  const [showTemplateSelector, setShowTemplateSelector] = useState(false);
  const [selectedTemplate, setSelectedTemplate] = useState<Template | null>(null);
  const [showVariableDialog, setShowVariableDialog] = useState(false);
  const fileInputRef = useRef<HTMLInputElement>(null);
  const { toast } = useToast();

  const { data: conversations = [] } = useQuery<ConversationWithContact[]>({
    queryKey: ["/api/conversations"],
  });

  const { data: messages = [], isLoading: messagesLoading } = useQuery<Message[]>({
    queryKey: ["/api/conversations", selectedConversationId, "messages"],
    enabled: !!selectedConversationId,
  });

  const { data: templates = [] } = useQuery<Template[]>({
    queryKey: ["/api/templates"],
  });

  const selectedConversation = conversations.find(c => c.id === selectedConversationId);

  const handleSendMessage = async () => {
    if (!messageText.trim() || !selectedConversationId || isSending) return;

    setIsSending(true);
    try {
      await apiRequest("POST", "/api/messages", {
        conversationId: selectedConversationId,
        contactId: selectedConversation?.contactId,
        content: messageText,
        type: "text",
        direction: "outbound",
        status: "sent",
        templateId: null,
        metadata: null,
      });

      setMessageText("");
      // Subtle success feedback - message status will show in bubble
    } catch (error) {
      toast({
        title: "Failed to send message",
        description: "There was an error sending your message. Please try again.",
        variant: "destructive",
      });
    } finally {
      setIsSending(false);
    }
  };

  const handleKeyPress = (e: React.KeyboardEvent) => {
    if (e.key === "Enter" && !e.shiftKey) {
      e.preventDefault();
      handleSendMessage();
    }
  };

  const handleFileUpload = () => {
    fileInputRef.current?.click();
  };

  const handleFileChange = async (e: React.ChangeEvent<HTMLInputElement>) => {
    const file = e.target.files?.[0];
    if (!file || !selectedConversationId) return;

    // Check file type and size
    const maxSize = 100 * 1024 * 1024; // 100MB limit
    if (file.size > maxSize) {
      toast({
        title: "File too large",
        description: "Please select a file smaller than 100MB.",
        variant: "destructive"
      });
      return;
    }

    const allowedTypes = ['image/jpeg', 'image/jpg', 'image/png', 'image/gif', 'video/mp4', 'video/mov', 'application/pdf', 'application/msword', 'application/vnd.openxmlformats-officedocument.wordprocessingml.document'];
    if (!allowedTypes.includes(file.type)) {
      toast({
        title: "Unsupported file type",
        description: "Please select an image, video, or document file.",
        variant: "destructive"
      });
      return;
    }

    setIsSending(true);
    try {
      // Convert file to base64
      const base64 = await new Promise<string>((resolve, reject) => {
        const reader = new FileReader();
        reader.onload = () => {
          const result = reader.result as string;
          resolve(result.split(',')[1]); // Remove data:mime;base64, prefix
        };
        reader.onerror = reject;
        reader.readAsDataURL(file);
      });

      // Determine media type
      let mediaType = 'document';
      if (file.type.startsWith('image/')) {
        mediaType = 'image';
      } else if (file.type.startsWith('video/')) {
        mediaType = 'video';
      }

      // Send media message
      await apiRequest("POST", "/api/messages/media", {
        conversationId: selectedConversationId,
        contactId: selectedConversation?.contactId,
        mediaType: file.type,
        mediaData: base64,
        filename: file.name,
        caption: "",
      });

      toast({
        title: "Media sent",
        description: `${file.name} sent successfully!`,
      });
    } catch (error) {
      toast({
        title: "Failed to send media",
        description: "There was an error sending your file. Please try again.",
        variant: "destructive",
      });
    } finally {
      setIsSending(false);
      // Reset file input
      if (fileInputRef.current) {
        fileInputRef.current.value = '';
      }
    }
  };

  const handleSendTemplate = async (templateId: string) => {
    if (!selectedConversationId || isSending) return;

    const template = templates.find(t => t.id === templateId);
    if (!template) {
      toast({
        title: "Template not found",
        description: "Please select a valid template.",
        variant: "destructive",
      });
      return;
    }

    // Check if template has variables
    const templateVariables = template.variables && Array.isArray(template.variables) ? template.variables as string[] : [];

    if (templateVariables.length > 0) {
      // Show variable input dialog
      setSelectedTemplate(template);
      setShowVariableDialog(true);
      setShowTemplateSelector(false);
      return;
    }

    // Send template without variables
    setIsSending(true);
    try {
      await apiRequest("POST", "/api/messages/template", {
        conversationId: selectedConversationId,
        contactId: selectedConversation?.contactId,
        templateId: templateId,
      });

      setShowTemplateSelector(false);
    } catch (error) {
      toast({
        title: "Failed to send template",
        description: "Please try again.",
        variant: "destructive",
      });
    } finally {
      setIsSending(false);
    }
  };

  const handleSendTemplateWithVariables = async (variables: Record<string, string>) => {
    if (!selectedConversationId || !selectedTemplate || isSending) return;

    setIsSending(true);
    try {
      await apiRequest("POST", "/api/messages/template-with-variables", {
        conversationId: selectedConversationId,
        contactId: selectedConversation?.contactId,
        templateId: selectedTemplate.id,
        variables: variables,
      });

      setShowVariableDialog(false);
      setSelectedTemplate(null);
    } catch (error) {
      toast({
        title: "Failed to send template",
        description: "Please try again.",
        variant: "destructive",
      });
    } finally {
      setIsSending(false);
    }
  };

  return (
    <div className="flex h-full bg-gray-100">
      {/* Chat List Panel - Left Side */}
      <div className="w-80 bg-white border-r border-gray-200 flex flex-col">
        <div className="bg-whatsapp text-white p-4 border-b border-gray-200">
          <div className="flex items-center justify-between mb-4">
            <h2 className="text-xl font-semibold text-white">Conversations</h2>
            <Button variant="ghost" size="icon" className="text-white hover:bg-green-600">
              <i className="fas fa-search" />
            </Button>
          </div>
          <div className="relative">
            <Input
              type="text"
              placeholder="Search conversations..."
              className="w-full pl-10"
              data-testid="input-search-conversations"
            />
            <i className="fas fa-search absolute left-3 top-1/2 transform -translate-y-1/2 text-gray-400" />
          </div>
        </div>

        <div className="flex-1 overflow-y-auto">
          <ChatList
            conversations={conversations}
            selectedConversationId={selectedConversationId}
            onConversationSelect={onConversationSelect}
          />
        </div>
      </div>

      {/* Main Chat Area */}
      <div className="flex-1 flex flex-col">
        {selectedConversation ? (
          <>
            {/* Chat Header */}
            <div className="bg-gray-50 p-4 border-b border-gray-200 flex items-center justify-between">
              <div className="flex items-center space-x-3">
                <div className="w-10 h-10 rounded-full bg-gradient-to-br from-green-400 to-green-600 flex items-center justify-center text-white shadow-md">
                  <span className="text-sm font-medium">
                    {selectedConversation.contact.name.charAt(0).toUpperCase()}
                  </span>
                </div>
                <div>
                  <h3 className="font-medium text-gray-900" data-testid="text-contact-name">
                    {selectedConversation.contact.name}
                  </h3>
                  <p className="text-xs text-gray-500">{selectedConversation.contact.phone}</p>
                </div>
              </div>
              <div className="flex items-center space-x-2">
                <Button variant="ghost" size="icon" className="text-gray-600">
                  <i className="fas fa-ellipsis-v" />
                </Button>
              </div>
            </div>

            {/* Messages Area */}
            <div className="flex-1 overflow-y-auto p-4 chat-bg">
              {messagesLoading ? (
                <div className="flex justify-center items-center h-full">
                  <div className="text-gray-500">Loading messages...</div>
                </div>
              ) : messages.length === 0 ? (
                <div className="flex justify-center items-center h-full">
                  <div className="text-gray-500">No messages yet. Start a conversation!</div>
                </div>
              ) : (
                <div className="space-y-4">
                  {messages.map((message) => (
                    <MessageBubble key={message.id} message={message} />
                  ))}
                </div>
              )}
            </div>

            {/* Message Input */}
            <div className="bg-white p-4 border-t border-gray-200">
              <div className="flex items-center space-x-3">
                <Button 
                  variant="ghost" 
                  size="icon" 
                  className="text-gray-600 hover:text-green-600"
                  onClick={handleFileUpload}
                  data-testid="button-upload-file"
                >
                  <i className="fas fa-paperclip" />
                </Button>
                <input
                  type="file"
                  ref={fileInputRef}
                  onChange={handleFileChange}
                  style={{ display: 'none' }}
                  accept="image/*,video/*,audio/*,.pdf,.doc,.docx"
                />

                {showTemplateSelector ? (
                  <div className="relative">
                    <Button 
                      variant="ghost" 
                      size="icon" 
                      className="text-green-600 bg-green-50" 
                      onClick={() => setShowTemplateSelector(false)}
                      data-testid="button-template-close"
                    >
                      <i className="fas fa-times" />
                    </Button>
                    <div className="absolute bottom-full left-0 mb-2 w-80 bg-white border rounded-lg shadow-lg p-3 z-50">
                      <div className="space-y-3">
                        <h4 className="font-medium text-sm">Send Template Message</h4>
                        {templates.length === 0 ? (
                          <p className="text-sm text-gray-500">No templates available. Sync templates from Facebook Business Manager.</p>
                        ) : (
                          <div className="space-y-2 max-h-40 overflow-y-auto">
                            {templates.map((template) => (
                              <Button
                                key={template.id}
                                variant="outline"
                                size="sm"
                                className="w-full justify-start text-left"
                                onClick={() => handleSendTemplate(template.id)}
                                disabled={isSending}
                              >
                                <div>
                                  <div className="font-medium">{template.name}</div>
                                  <div className="text-xs text-gray-500 truncate">
                                    {template.content.substring(0, 50)}...
                                  </div>
                                </div>
                              </Button>
                            ))}
                          </div>
                        )}
                      </div>
                    </div>
                  </div>
                ) : (
                  <Button 
                    variant="ghost" 
                    size="icon" 
                    className="text-gray-600 hover:text-green-600" 
                    onClick={() => setShowTemplateSelector(true)}
                    data-testid="button-template"
                  >
                    <i className="fas fa-file-alt" />
                  </Button>
                )}

                <div className="flex-1 relative">
                  <Input
                    type="text"
                    placeholder="Type a message..."
                    value={messageText}
                    onChange={(e) => setMessageText(e.target.value)}
                    onKeyPress={handleKeyPress}
                    className="w-full pl-4 pr-12 py-3 rounded-full"
                    data-testid="input-message"
                  />
                  <Button
                    size="icon"
                    className="absolute right-2 top-1/2 transform -translate-y-1/2 rounded-full whatsapp-green"
                    onClick={handleSendMessage}
                    disabled={!messageText.trim() || isSending}
                    data-testid="button-send-message"
                  >
                    <i className="fas fa-paper-plane" />
                  </Button>
                </div>
              </div>
            </div>
          </>
        ) : (
          <div className="flex-1 flex items-center justify-center">
            <div className="text-center text-gray-500">
              <i className="fas fa-comments text-6xl mb-4 text-gray-300" />
              <h3 className="text-xl font-medium mb-2">Welcome to WhatsApp Business</h3>
              <p>Select a conversation to start messaging</p>
            </div>
          </div>
        )}
      </div>

      {/* Template Variable Dialog */}
      {selectedTemplate && (
        <TemplateVariableDialog
          template={selectedTemplate}
          isOpen={showVariableDialog}
          onClose={() => setShowVariableDialog(false)}
          onSend={handleSendTemplateWithVariables}
          isSending={isSending}
        />
      )}
    </div>
  );
}

