                </div>
              </div>
            </div>
          )}
          {message.type === "media" && metadata && (
            <div className="mt-2">
              {metadata.mediaType?.startsWith('image/') && (
                <div className="relative">
                  {metadata.mediaUrl ? (
                    <img
                      src={metadata.mediaUrl}
                      alt={metadata.filename || "Image"}
                      className="max-w-full max-h-80 rounded-lg object-cover cursor-pointer hover:opacity-90 transition-opacity"
                      onClick={() => window.open(metadata.mediaUrl, '_blank')}
                      loading="lazy"
                    />
                  ) : (
                    <div className="bg-gray-100 rounded-lg p-4 flex items-center justify-center min-h-[100px]">
                      <span className="text-gray-600">ðŸ–¼ï¸ Image</span>
                    </div>
                  )}
                  {metadata.filename && (
                    <div className="text-xs text-gray-500 mt-1">
                      ðŸ“Ž {metadata.filename}
                    </div>
                  )}
                </div>
              )}

              {metadata.mediaType?.startsWith('video/') && (
                <div className="relative">
                  {metadata.mediaUrl ? (
                    <video
                      src={metadata.mediaUrl}
                      controls
                      className="max-w-full max-h-80 rounded-lg"
                      poster={metadata.thumbnailUrl}
                    >
                      Your browser does not support the video tag.
                    </video>
                  ) : (
                    <div className="bg-gray-100 rounded-lg p-4 flex items-center justify-center min-h-[100px]">
                      <span className="text-gray-600">ðŸŽ¥ Video</span>
                    </div>
                  )}
                  {metadata.filename && (
                    <div className="text-xs text-gray-500 mt-1">
                      ðŸ“Ž {metadata.filename}
                    </div>
                  )}
                </div>
              )}

              {metadata.mediaType && !metadata.mediaType.startsWith('image/') && !metadata.mediaType.startsWith('video/') && (
                <div className="bg-gray-50 rounded border p-3">
                  <div className="flex items-center">
                    <span className="text-gray-400 text-lg">ðŸ“„</span>
                    <div className="ml-2">
                      <div className="text-sm font-medium text-gray-700">
                        {metadata.filename || "Document"}
                      </div>
                      <div className="text-xs text-gray-500">
                        {metadata.mediaType || "Unknown type"}
                      </div>
                      {metadata.mediaUrl && (
                        <a
                          href={metadata.mediaUrl}
                          target="_blank"
                          rel="noopener noreferrer"
                          className="text-xs text-blue-500 hover:underline"
                        >
                          Download
                        </a>
                      )}
                    </div>
                  </div>
                </div>
              )}

              {metadata.caption && (
                <div className="text-sm text-gray-600 mt-2">
                  {metadata.caption}
                </div>
              )}
            </div>
          )}

          {/* Template metadata */}
          {message.type === "template" && metadata && (
            <div className="mt-2">
              {metadata.variables && Object.keys(metadata.variables).length > 0 && (
                <div className="text-xs text-gray-500 mt-1">
                  <span className="font-medium">Variables:</span>
                  {Object.entries(metadata.variables).map(([key, value]) => (
                    <div key={key} className="ml-2">
                      {key}: {String(value)}
                    </div>
                  ))}
                </div>
              )}
            </div>
          )}

          {/* Legacy attachment metadata */}
          {metadata && metadata.attachment && (
            <div className="mt-2 p-2 bg-gray-50 rounded border">
              <div className="text-sm font-medium text-gray-700">
                ðŸ“‹ {metadata.attachment.name || "Attachment"}
              </div>
              <div className="text-xs text-gray-500">
                {metadata.attachment.type || "File"} â€¢ {metadata.attachment.size || "Unknown size"}
              </div>
            </div>
          )}

          <div className={cn(
            "flex items-center mt-1 space-x-1",
            isOutbound ? "justify-end" : "justify-start"
          )}>
            <span className="text-xs text-gray-500">
              {formatTime(message.timestamp || new Date())}
            </span>
            {isOutbound && getStatusIcon()}
          </div>
        </div>
      </div>
    </div>
  );
}

client/src/components/contacts/contact-m.content:
import { useState } from "react";
import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { Dialog, DialogContent, DialogDescription, DialogHeader, DialogTitle, DialogTrigger } from "@/components/ui/dialog";
import { Badge } from "@/components/ui/badge";
import { useToast } from "@/hooks/use-toast";
import { apiRequest } from "@/lib/queryClient";
import { Contact } from "@shared/schema";

export default function ContactManager() {
  const [isCreateDialogOpen, setIsCreateDialogOpen] = useState(false);
  const [editingContact, setEditingContact] = useState<Contact | null>(null);
  const [searchTerm, setSearchTerm] = useState("");
  const [filterGroup, setFilterGroup] = useState("all");
  const [formData, setFormData] = useState({
    name: "",
    phone: "",
    email: "",
    group: "customer",
  });

  const { toast } = useToast();
  const queryClient = useQueryClient();

  const { data: contacts = [], isLoading } = useQuery<Contact[]>({
    queryKey: ["/api/contacts"],
  });

  const createMutation = useMutation({
    mutationFn: async (data: any) => {
      const response = await apiRequest("POST", "/api/contacts", data);
      return response.json();
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/contacts"] });
      setIsCreateDialogOpen(false);
      resetForm();
      toast({
        title: "Contact created",
        description: "Your contact has been created successfully.",
      });
    },
    onError: (error: Error) => {
      toast({
        title: "Failed to create contact",
        description: error.message || "There was an error creating your contact.",
        variant: "destructive",
      });
    },
  });

  const updateMutation = useMutation({
    mutationFn: async ({ id, data }: { id: string; data: any }) => {
      const response = await apiRequest("PUT", `/api/contacts/${id}`, data);
      return response.json();
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/contacts"] });
      setEditingContact(null);
      resetForm();
      toast({
        title: "Contact updated",
        description: "Your contact has been updated successfully.",
      });
    },
    onError: (error: Error) => {
      toast({
        title: "Failed to update contact",
        description: error.message || "There was an error updating your contact.",
        variant: "destructive",
      });
    },
  });

  const deleteMutation = useMutation({
    mutationFn: async (id: string) => {
      await apiRequest("DELETE", `/api/contacts/${id}`);
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/contacts"] });
      toast({
        title: "Contact deleted",
        description: "Your contact has been deleted successfully.",
      });
    },
    onError: (error: Error) => {
      toast({
        title: "Failed to delete contact",
        description: error.message || "There was an error deleting your contact.",
        variant: "destructive",
      });
    },
  });

  const resetForm = () => {
    setFormData({
      name: "",
      phone: "",
      email: "",
      group: "customer",
    });
  };

  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();

    if (editingContact) {
      updateMutation.mutate({ id: editingContact.id, data: formData });
    } else {
      createMutation.mutate(formData);
    }
  };

  const handleEdit = (contact: Contact) => {
    setEditingContact(contact);
    setFormData({
      name: contact.name,
      phone: contact.phone,
      email: contact.email || "",
      group: contact.group || "customer",
    });
    setIsCreateDialogOpen(true);
  };

  const handleCloseDialog = () => {
    setIsCreateDialogOpen(false);
    setEditingContact(null);
    resetForm();
  };

  // Filter contacts based on search and group
  const filteredContacts = contacts.filter(contact => {
    const matchesSearch = contact.name.toLowerCase().includes(searchTerm.toLowerCase()) ||
                         contact.phone.includes(searchTerm);
    const matchesGroup = filterGroup === "all" || (contact.group || "customer") === filterGroup;
    return matchesSearch && matchesGroup;
  });

  const getGroupColor = (group: string) => {
    const colors: Record<string, string> = {
      customer: "bg-blue-100 text-blue-800",
      prospect: "bg-yellow-100 text-yellow-800",
      vip: "bg-purple-100 text-purple-800",
      lead: "bg-green-100 text-green-800",
    };
    return colors[group] || "bg-gray-100 text-gray-800";
  };

  const getUniqueGroups = () => {
    const groups = [...new Set(contacts.map(c => c.group || "customer"))];
    return groups;
  };

  const formatLastContact = (date: Date | null) => {
    if (!date) return "Never";

    const now = new Date();
    const contactDate = new Date(date);
    const diffMs = now.getTime() - contactDate.getTime();
    const diffHours = diffMs / (1000 * 60 * 60);
    const diffDays = diffMs / (1000 * 60 * 60 * 24);

    if (diffHours < 1) {
      return "Just now";
    } else if (diffHours < 24) {
      return "Today";
    } else if (diffDays < 7) {
      return `${Math.floor(diffDays)} days ago`;
    } else {
      return contactDate.toLocaleDateString();
    }
  };

  if (isLoading) {
    return (
      <div className="flex items-center justify-center h-full">
        <div className="text-center">
          <div className="w-8 h-8 border-2 border-primary border-t-transparent rounded-full animate-spin mx-auto mb-2"></div>
          <p className="text-muted-foreground">Loading contacts...</p>
        </div>
      </div>
    );
  }

  return (
    <div className="p-6 max-w-6xl mx-auto">
      <div className="flex items-center justify-between mb-6">
        <div>
          <h2 className="text-2xl font-bold text-gray-800">Contacts</h2>
          <p className="text-gray-600 mt-1">Manage your WhatsApp business contacts</p>
        </div>
        <div className="flex space-x-3">
          <Button variant="outline" className="text-gray-600">
            <i className="fas fa-upload mr-2" />
            Import CSV
          </Button>
          <Dialog open={isCreateDialogOpen} onOpenChange={setIsCreateDialogOpen}>
            <DialogTrigger asChild>
              <Button className="whatsapp-green" data-testid="button-new-contact">
                <i className="fas fa-plus mr-2" />
                Add Contact
              </Button>
            </DialogTrigger>
            <DialogContent>
              <DialogHeader>
                <DialogTitle>{editingContact ? "Edit Contact" : "Add New Contact"}</DialogTitle>
                <DialogDescription>
                  {editingContact ? "Update contact information below." : "Enter the contact details below."}
                </DialogDescription>
              </DialogHeader>
              <form onSubmit={handleSubmit} className="space-y-4">
                <div>
                  <Label htmlFor="name">Name</Label>
                  <Input
                    id="name"
                    value={formData.name}
                    onChange={(e) => setFormData(prev => ({ ...prev, name: e.target.value }))}
                    placeholder="Enter contact name"
                    required
                    data-testid="input-contact-name"
                  />
                </div>
                <div>
                  <Label htmlFor="phone">Phone Number</Label>
                  <Input
                    id="phone"
                    value={formData.phone}
                    onChange={(e) => setFormData(prev => ({ ...prev, phone: e.target.value }))}
                    placeholder="+1234567890"
                    required
                    data-testid="input-contact-phone"
                  />
                </div>
                <div>
                  <Label htmlFor="email">Email (Optional)</Label>
                  <Input
                    id="email"
                    type="email"
                    value={formData.email}
                    onChange={(e) => setFormData(prev => ({ ...prev, email: e.target.value }))}
                    placeholder="email@example.com"
                    data-testid="input-contact-email"
                  />
                </div>
                <div>
                  <Label htmlFor="group">Group</Label>
                  <Select value={formData.group} onValueChange={(value) => setFormData(prev => ({ ...prev, group: value }))}>
                    <SelectTrigger data-testid="select-contact-group">
                      <SelectValue placeholder="Select group" />
                    </SelectTrigger>
                    <SelectContent>
                      <SelectItem value="customer">Customer</SelectItem>
                      <SelectItem value="prospect">Prospect</SelectItem>
                      <SelectItem value="vip">VIP</SelectItem>
                      <SelectItem value="lead">Lead</SelectItem>
                    </SelectContent>
                  </Select>
                </div>
                <div className="flex justify-end space-x-2 pt-4">
                  <Button
                    type="button"
                    variant="outline"
                    onClick={handleCloseDialog}
                    data-testid="button-cancel-contact"
                  >
                    Cancel
                  </Button>
                  <Button
                    type="submit"
                    className="whatsapp-green"
                    disabled={createMutation.isPending || updateMutation.isPending}
                    data-testid="button-save-contact"
                  >
                    {(createMutation.isPending || updateMutation.isPending) ? "Saving..." : (editingContact ? "Update" : "Add")}
                  </Button>
                </div>
              </form>
            </DialogContent>
          </Dialog>
        </div>
      </div>

      {/* Search and Filters */}
      <Card className="mb-6">
        <CardContent className="pt-6">
          <div className="flex items-center space-x-4">
            <div className="flex-1 relative">
              <Input
                type="text"
                placeholder="Search contacts..."
                value={searchTerm}
                onChange={(e) => setSearchTerm(e.target.value)}
                className="pl-10"
                data-testid="input-search-contacts"
              />
              <i className="fas fa-search absolute left-3 top-1/2 transform -translate-y-1/2 text-gray-400" />
            </div>
            <Select value={filterGroup} onValueChange={setFilterGroup}>
              <SelectTrigger className="w-48" data-testid="select-filter-group">
                <SelectValue placeholder="All Groups" />
              </SelectTrigger>
              <SelectContent>
                <SelectItem value="all">All Groups</SelectItem>
                {getUniqueGroups().map((group) => (
                  <SelectItem key={group} value={group}>
                    {group.charAt(0).toUpperCase() + group.slice(1)}
                  </SelectItem>
                ))}
              </SelectContent>
            </Select>
          </div>
        </CardContent>
      </Card>

      {/* Contacts List */}
      <Card>
        <CardContent className="p-0">
          {filteredContacts.length === 0 ? (
            <div className="text-center py-12">
              <i className="fas fa-address-book text-6xl text-gray-300 mb-4" />
              <h3 className="text-xl font-medium text-gray-500 mb-2">
                {contacts.length === 0 ? "No contacts yet" : "No contacts match your search"}
              </h3>
              <p className="text-gray-400 mb-4">
                {contacts.length === 0 ? "Add your first contact to get started" : "Try adjusting your search or filters"}
              </p>
              {contacts.length === 0 && (
                <Button className="whatsapp-green" onClick={() => setIsCreateDialogOpen(true)}>
                  <i className="fas fa-plus mr-2" />
                  Add Contact
                </Button>
              )}
            </div>
          ) : (
            <div className="divide-y divide-gray-200">
              {filteredContacts.map((contact) => (
                <div
                  key={contact.id}
                  className="p-4 hover:bg-gray-50 transition-colors"
                  data-testid={`contact-row-${contact.id}`}
                >
                  <div className="flex items-center justify-between">
                    <div className="flex items-center space-x-4">
                      <img
                        src={contact.profileImageUrl || "https://images.unsplash.com/photo-1507003211169-0a1dd7228f2d?ixlib=rb-4.0.3&w=48&h=48&fit=crop&crop=face"}
                        alt={contact.name}
                        className="w-12 h-12 rounded-full object-cover"
                      />
                      <div>
                        <h3 className="font-medium text-gray-900">{contact.name}</h3>
                        <p className="text-sm text-gray-600">{contact.phone}</p>
                        {contact.email && (
                          <p className="text-sm text-gray-500">{contact.email}</p>
                        )}
                        <div className="flex items-center space-x-2 mt-1">
                          <Badge className={getGroupColor(contact.group)}>
                            {contact.group}
                          </Badge>
                          <span className="text-xs text-gray-500">
                            Last contact: {formatLastContact(contact.lastContact)}
                          </span>
                        </div>
                      </div>
                    </div>
                    <div className="flex items-center space-x-2">
                      <Button
                        className="whatsapp-green"
                        size="sm"
                        data-testid={`button-message-contact-${contact.id}`}
                      >
                        Message
                      </Button>
                      <Button
                        variant="ghost"
                        size="icon"
                        onClick={() => handleEdit(contact)}
                        data-testid={`button-edit-contact-${contact.id}`}
                      >
                        <i className="fas fa-edit text-gray-400 hover:text-gray-600" />
                      </Button>
                      <Button
                        variant="ghost"
                        size="icon"
                        onClick={() => deleteMutation.mutate(contact.id)}
                        data-testid={`button-delete-contact-${contact.id}`}
                      >
                        <i className="fas fa-trash text-gray-400 hover:text-red-500" />
                      </Button>
                    </div>
                  </div>
                </div>
              ))}
            </div>
          )}
        </CardContent>
      </Card>
    </div>
  );
}

client/src/components/templates/template.content:
import { useState } from "react";
import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Textarea } from "@/components/ui/textarea";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { Dialog, DialogContent, DialogDescription, DialogHeader, DialogTitle, DialogTrigger } from "@/components/ui/dialog";
import { Badge } from "@/components/ui/badge";
import { useToast } from "@/hooks/use-toast";
import { apiRequest } from "@/lib/queryClient";
import { Template } from "@shared/schema";

export default function TemplateManager() {
  const [isCreateDialogOpen, setIsCreateDialogOpen] = useState(false);
  const [editingTemplate, setEditingTemplate] = useState<Template | null>(null);
  const [formData, setFormData] = useState({
    name: "",
    category: "general",
    content: "",
  });

  const { toast } = useToast();
  const queryClient = useQueryClient();

  const { data: templates = [], isLoading } = useQuery<Template[]>({
    queryKey: ["/api/templates"],
  });

  const createMutation = useMutation({
    mutationFn: async (data: any) => {
      const response = await apiRequest("POST", "/api/templates", data);
      return response.json();
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/templates"] });
      setIsCreateDialogOpen(false);
      resetForm();
      toast({
        title: "Template created",
        description: "Your template has been created successfully.",
      });
    },
    onError: (error: Error) => {
      toast({
        title: "Failed to create template",
        description: error.message || "There was an error creating your template.",
        variant: "destructive",
      });
    },
  });

  const updateMutation = useMutation({
    mutationFn: async ({ id, data }: { id: string; data: any }) => {
      const response = await apiRequest("PUT", `/api/templates/${id}`, data);
      return response.json();
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/templates"] });
      setEditingTemplate(null);
      resetForm();
      toast({
        title: "Template updated",
        description: "Your template has been updated successfully.",
      });
    },
    onError: (error: Error) => {
      toast({
        title: "Failed to update template",
        description: error.message || "There was an error updating your template.",
        variant: "destructive",
      });
    },
  });

  const deleteMutation = useMutation({
    mutationFn: async (id: string) => {
      await apiRequest("DELETE", `/api/templates/${id}`);
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/templates"] });
      toast({
        title: "Template deleted",
        description: "Your template has been deleted successfully.",
      });
    },
    onError: (error: Error) => {
      toast({
        title: "Failed to delete template",
        description: error.message || "There was an error deleting your template.",
        variant: "destructive",
      });
    },
  });

  const syncMutation = useMutation({
    mutationFn: async () => {
      const response = await apiRequest("POST", "/api/templates/sync");
      return response.json();
    },
    onSuccess: (data) => {
      queryClient.invalidateQueries({ queryKey: ["/api/templates"] });
      toast({
        title: "Templates synced successfully",
        description: `${data.synced} templates synchronized from Facebook Business Manager`,
      });
    },
    onError: (error: Error) => {
      toast({
        title: "Failed to sync templates",
        description: error.message || "Check your WhatsApp configuration in Settings",
        variant: "destructive",
      });
    },
  });

  const resetForm = () => {
    setFormData({
      name: "",
      category: "general",
      content: "",
    });
  };

  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();

    // Extract variables from content
    const variables = formData.content.match(/\{\{([^}]+)\}\}/g)?.map(v => v.slice(2, -2)) || [];

    const templateData = {
      ...formData,
      variables,
    };

    if (editingTemplate) {
      updateMutation.mutate({ id: editingTemplate.id, data: templateData });
    } else {
      createMutation.mutate(templateData);
    }
  };

  const handleEdit = (template: Template) => {
    setEditingTemplate(template);
    setFormData({
      name: template.name,
      category: template.category || "general",
      content: template.content,
    });
    setIsCreateDialogOpen(true);
  };

  const handleCloseDialog = () => {
    setIsCreateDialogOpen(false);
    setEditingTemplate(null);
    resetForm();
  };

  const getCategoryColor = (category: string) => {
    const colors: Record<string, string> = {
      greeting: "bg-blue-100 text-blue-800",
      marketing: "bg-green-100 text-green-800",
      transactional: "bg-purple-100 text-purple-800",
      general: "bg-gray-100 text-gray-800",
    };
    return colors[category] || colors.general;
  };

  if (isLoading) {
    return (
      <div className="flex items-center justify-center h-full">
        <div className="text-center">
          <div className="w-8 h-8 border-2 border-primary border-t-transparent rounded-full animate-spin mx-auto mb-2"></div>
          <p className="text-muted-foreground">Loading templates...</p>
        </div>
      </div>
    );
  }

  return (
    <div className="p-6 max-w-6xl mx-auto">
      <div className="flex items-center justify-between mb-6">
        <div>
          <h2 className="text-2xl font-bold text-gray-800">Message Templates</h2>
          <p className="text-gray-600 mt-1">Create and manage your WhatsApp message templates</p>
        </div>
        <div className="flex gap-2">
          <Button 
            variant="outline" 
            onClick={() => syncMutation.mutate()}
            disabled={syncMutation.isPending}
            data-testid="button-sync-templates"
          >
            <i className="fas fa-sync-alt mr-2" />
            {syncMutation.isPending ? "Syncing..." : "Sync from Facebook"}
          </Button>
          <Dialog open={isCreateDialogOpen} onOpenChange={setIsCreateDialogOpen}>
            <DialogTrigger asChild>
              <Button className="whatsapp-green" data-testid="button-new-template">
                <i className="fas fa-plus mr-2" />
                New Template
              </Button>
            </DialogTrigger>
          <DialogContent className="max-w-2xl">
            <DialogHeader>
              <DialogTitle>{editingTemplate ? "Edit Template" : "Create New Template"}</DialogTitle>
              <DialogDescription>
                {editingTemplate ? "Update your template details below." : "Create a new message template with variables."}
              </DialogDescription>
            </DialogHeader>
            <form onSubmit={handleSubmit} className="space-y-4">
              <div className="grid grid-cols-2 gap-4">
                <div>
                  <Label htmlFor="name">Template Name</Label>
                  <Input
                    id="name"
                    value={formData.name}
                    onChange={(e) => setFormData(prev => ({ ...prev, name: e.target.value }))}
                    placeholder="Enter template name"
                    required
                    data-testid="input-template-name"
                  />
                </div>
                <div>
                  <Label htmlFor="category">Category</Label>
                  <Select value={formData.category} onValueChange={(value) => setFormData(prev => ({ ...prev, category: value }))}>
                    <SelectTrigger data-testid="select-template-category">
                      <SelectValue placeholder="Select category" />
                    </SelectTrigger>
                    <SelectContent>
                      <SelectItem value="general">General</SelectItem>
                      <SelectItem value="greeting">Greeting</SelectItem>
                      <SelectItem value="marketing">Marketing</SelectItem>
                      <SelectItem value="transactional">Transactional</SelectItem>
                    </SelectContent>
                  </Select>
                </div>
              </div>
              <div>
                <Label htmlFor="content">Template Content</Label>
                <Textarea
                  id="content"
                  value={formData.content}
                  onChange={(e) => setFormData(prev => ({ ...prev, content: e.target.value }))}
                  placeholder="Enter your template content. Use {{variable_name}} for variables."
                  rows={6}
                  required
                  data-testid="textarea-template-content"
                />
                <p className="text-sm text-gray-500 mt-1">
                  Use double curly braces for variables, e.g., {"{{customer_name}}"} or {"{{product_name}}"}
                </p>
              </div>
              <div className="flex justify-end space-x-2 pt-4">
                <Button
                  type="button"
                  variant="outline"
                  onClick={handleCloseDialog}
                  data-testid="button-cancel-template"
                >
                  Cancel
                </Button>
                <Button
                  type="submit"
                  className="whatsapp-green"
                  disabled={createMutation.isPending || updateMutation.isPending}
                  data-testid="button-save-template"
                >
                  {(createMutation.isPending || updateMutation.isPending) ? "Saving..." : (editingTemplate ? "Update" : "Create")}
                </Button>
              </div>
            </form>
          </DialogContent>
          </Dialog>
        </div>
      </div>

      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
        {templates.length === 0 ? (
          <div className="col-span-full text-center py-12">
            <i className="fas fa-file-alt text-6xl text-gray-300 mb-4" />
            <h3 className="text-xl font-medium text-gray-500 mb-2">No templates yet</h3>
            <p className="text-gray-400 mb-4">Create your first message template to get started</p>
            <Button className="whatsapp-green" onClick={() => setIsCreateDialogOpen(true)}>
              <i className="fas fa-plus mr-2" />
              Create Template
            </Button>
          </div>
        ) : (
          templates.map((template) => (
            <Card key={template.id} className="hover:shadow-md transition-shadow" data-testid={`template-card-${template.id}`}>
              <CardHeader className="pb-3">
                <div className="flex items-center justify-between">
                  <CardTitle className="text-lg">{template.name}</CardTitle>
                  <div className="flex space-x-2">
                    <Button
                      variant="ghost"
                      size="icon"
                      onClick={() => handleEdit(template)}
                      data-testid={`button-edit-template-${template.id}`}
                    >
                      <i className="fas fa-edit text-gray-400 hover:text-gray-600" />
                    </Button>
                    <Button
                      variant="ghost"
                      size="icon"
                      onClick={() => deleteMutation.mutate(template.id)}
                      data-testid={`button-delete-template-${template.id}`}
                    >
                      <i className="fas fa-trash text-gray-400 hover:text-red-500" />
                    </Button>
                  </div>
                </div>
                <Badge className={getCategoryColor(template.category || "general")}>
                  {template.category || "general"}
                </Badge>
              </CardHeader>
              <CardContent>
                <div className="bg-gray-50 p-3 rounded text-sm text-gray-700 mb-4 min-h-[80px]">
                  {template.content}
                </div>
                {template.variables && Array.isArray(template.variables) && template.variables.length > 0 && (
                  <div className="mb-4">
                    <p className="text-xs text-gray-500 mb-2">Variables:</p>
                    <div className="flex flex-wrap gap-1">
                      {template.variables.map((variable, index) => (
                        <Badge key={index} variant="outline" className="text-xs">
                          {variable}
                        </Badge>
                      ))}
                    </div>
                  </div>
                )}
                <div className="flex space-x-2">
                  <Button className="flex-1 whatsapp-green text-sm" data-testid={`button-use-template-${template.id}`}>
                    Use Template
                  </Button>
                  <Button variant="outline" className="px-3 text-sm">
                    Preview
                  </Button>
                </div>
              </CardContent>
            </Card>
          ))
        )}
      </div>
    </div>
  );
}

